    const newNotification = {
      id: Date.now(),
      message,
      type,
      timestamp: new Date().toLocaleTimeString()
    };
    setNotifications(prev => [newNotification, ...prev].slice(0, 20));
  };

  const clearNotifications = () => {
    if (notifications.length > 0) {
      addNotification('üóëÔ∏è All notifications cleared', 'info');
      setNotifications([]);
    }
  };

  const getNotificationsCount = () => {
    return notifications.filter(n => n.type === 'alert').length;
  };

  const setPriceAlert = () => {
    if (targetPrice) {
      const numericPrice = parseInt(targetPrice.replace(/[^0-9]/g, ''));
      if (numericPrice > 0) {
        addNotification(`üîî Price alert set for ‚Çπ${numericPrice.toLocaleString()}`, 'success');
      } else {
        addNotification('‚ùå Please enter a valid price', 'error');
      }
    } else {
      addNotification('‚ùå Please enter a target price first', 'error');
    }
  };

  // Filter products by category and search
  const filteredProducts = Object.entries(products).filter(([id, product]) => {
    const matchesCategory = activeCategory === 'all' || product.category === activeCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  // Auto-clear old notifications
  useEffect(() => {
    const interval = setInterval(() => {
      setNotifications(prev => prev.filter(notification => {
        const notificationTime = new Date(notification.id);
        const currentTime = new Date();
        return (currentTime - notificationTime) < 300000; // 5 minutes
      }));
    }, 60000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
