                      onClick={() => {
                        setSelectedProduct(id);
                        addNotification(`üì± Selected: ${product.name}`, 'info');
                        if (realTimeUpdates) {
                          addNotification('üîÑ Real-time monitoring active for this product', 'success');
                        }
                      }}
                    >
                      <div className="product-info">
                        <div className="product-name">{product.name}</div>
                        <div className="product-category">{categories[product.category]}</div>
                        <div className="product-base-price">
                          MRP: ‚Çπ{product.basePrice.toLocaleString()}
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>

            <div className="control-section">
              <h3>üîî Price Alert</h3>
              <div className="form-group">
                <label>Notify me when price drops below:</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Enter target price (e.g., 45000)"
                  value={targetPrice}
                  onChange={(e) => setTargetPrice(e.target.value.replace(/[^0-9]/g, ''))}
                />
              </div>
              <button 
                className="btn btn-secondary"
                onClick={setPriceAlert}
              >
                üéØ Set Price Alert
              </button>
            </div>

            <div className="control-section">
              <h3>‚ö° Real-Time Controls</h3>
              <button 
                className={`btn ${realTimeUpdates ? 'btn-warning' : 'btn-success'}`}
                onClick={toggleRealTimeUpdates}
              >
                {realTimeUpdates ? '‚è∏Ô∏è Pause Real-Time' : 'üî¥ Start Real-Time'}
              </button>

              <button 
                className="btn btn-primary" 
                onClick={scanPrices}
                disabled={isScanning}
              >
                {isScanning ? 'üîÑ Scanning...' : 'üîç Scan Now'}
              </button>

              <button 
                className="btn btn-secondary"
                onClick={clearNotifications}
                disabled={notifications.length === 0}
              >
                üóëÔ∏è Clear Notifications ({notifications.length})
              </button>
            </div>

            <div className="status-bar">
              üìä {status}
              {realTimeUpdates && <span className="real-time-badge">LIVE</span>}
            </div>
          </div>

          {/* Right Panel - Results */}
          <div className="results-panel">
            <div className="results-header">
              <h2 className="results-title">
                üìä Live Price Comparison
                {results?.isRealTimeUpdate && <span className="real-time-update">üîÑ Updated</span>}
              </h2>
              {results && (
                <span className="alert-badge">
                  Updated: {results.lastUpdate}
                </span>
              )}
            </div>

            {/* Loading */}
            {isScanning && (
              <div className="loading">
                <div className="spinner"></div>
                <p>{status}</p>
                <div className="scanning-progress">
                  <div className="progress-bar">
                    <div className="progress-fill"></div>
                  </div>
                </div>
              </div>
            )}

            {/* Results */}
            {results && !isScanning && (
              <div className="price-results">
                <div className="product-header">
                  <img 
                    src={results.image} 
                    alt={results.product}
                    className="product-image-large"
                  />
                  <div className="product-info">
                    <h3>{results.product}</h3>
                    <p className="product-category">{categories[results.category]}</p>
                    <div className="product-scan-info">
                      Last scan: {results.scanTime}
                      {realTimeUpdates && <span className="auto-scan"> ‚Ä¢ Auto-scanning</span>}
                    </div>
                  </div>
                </div>

                <div className="price-cards">
                  {Object.entries(results.prices).map(([website, data]) => (
