                        </div>
                        <div className="website-rating">‚≠ê {data.rating}</div>
                      </div>

                      <div className="price-amount">{data.price}</div>

                      {/* Price Change Indicator */}
                      {data.priceChange !== 0 && (
                        <div className={`price-change ${data.priceChange < 0 ? 'price-drop' : 'price-rise'}`}>
                          {data.priceChange < 0 ? 'üìâ' : 'üìà'} 
                          ‚Çπ{Math.abs(data.priceChange).toLocaleString()}
